<script>


console.log("Back render!");
document.getElementById("me").style.display="flex";
document.getElementById("me").style.visibility="unset";


var currentText = 0;
var textElements = Array.from(document.getElementById('textContainer').querySelectorAll('.definition'));
var totalTexts = textElements.length;
var isTouchScrolling = false;

function iOS() {
  return [
    'iPad Simulator',
    'iPhone Simulator',
    'iPod Simulator',
    'iPad',
    'iPhone',
    'iPod'
  ].includes(navigator.platform)
  // iPad on iOS 13 detection
  || (navigator.userAgent.includes("Mac") && "ontouchend" in document)
}

// Function to update the displayed text and indicator
function updateText() {
	console.log("updating");
  textElements.forEach(function (element, index) {
    if (index === currentText) {
      element.style.display = 'block';
    } else {
      element.style.display = 'none';
    }
  });

  // Update the indicator
  var indicator = document.getElementById('textIndicator');
  indicator.textContent = '(' + (currentText + 1) + '/' + totalTexts + ')';
}

// Function to go to the previous text
function goToPrevText() {
console.log("prev");
console.log(currentText);
  currentText = (currentText - 1 + totalTexts) % totalTexts;
  updateText();
}

// Function to go to the next text
function goToNextText() {
console.log("next");
console.log(currentText);

  currentText = (currentText + 1) % totalTexts;
  updateText();
}

function cleanup() {
  document.getElementById("events").removeEventListener('click', handleCardClick);
  document.getElementById("events").removeEventListener('touchstart', handleTouchStart);
  document.getElementById("events").removeEventListener('touchend', handleTouchEnd);
}

// Event listener for mouse click
function handleCardClick(e) {
  var target = e.target;
  var windowWidth = window.innerWidth;
  var clickX = e.clientX;

  // Check if the click occurred outside the textContainer
  if (!isTouchScrolling) {
    if (clickX < windowWidth / 2) {
      goToPrevText(); // Clicked on the left side of the page and not during touch scroll
    } else {
      goToNextText(); // Clicked on the right side of the page and not during touch scroll
    }
  }
}


// Event listener for touchstart
function handleTouchStart() {
  isTouchScrolling = true;
}

// Event listener for touchend
function handleTouchEnd() {
  isTouchScrolling = false;
}

// Attach event listeners to the card
function attachEventListeners() {
console.log("adding listeners");

if (0){
document.getElementById("events").addEventListener('click touchstart', handleCardClick);
  document.getElementById("events").addEventListener('tap', handleCardClick);
}else{
document.getElementById("events").addEventListener('click', handleCardClick);
};


  document.getElementById("events").addEventListener('touchstart', handleTouchStart);
  document.getElementById("events").addEventListener('touchend', handleTouchEnd);
}

// Initial setup
//cleanup(); // Clean up any previous event listeners
attachEventListeners(); // Attach event listeners to the current card
updateText(); // Initial text update

</script>

